 areaTitle: "Content Column Options",
 init: function($container) {
 var contentbgcolor = CP_Customizer.createControl.color(
 this.getPrefixed('contentbgcolor'),
 $container, {
 value: '#ffffff',
 label: 'Content Column Color'
 });
 this.addToControlsList(contentbgcolor);
 },
 update: function(data) {
 var dataId = '[data-id=' + data.section.attr('data-id') + '] ',
 selector ='.content-col',
