module.exports = {
sectionArea: {
areaTitle: "WooCommerce Section Options",
init: function($container) {
var color = CP_Customizer.createControl.color(
this.getPrefixed('color'),
$container, {
value: '#ffffff',
label: 'Card Border Color'
});
this.addToControlsList(color);
},
update: function(data) {
var dataId = '[data-id=' + data.section.attr('data-id') + ']',
selector =' .woocommerce ul.products li.product .ope-woo-card-item.card[class*=bottom-border-]',
item = data.section.find(selector).eq(0),
styleSelector =  dataId + selector,
currentColor = CP_Customizer.contentStyle.getNodeProp(item, styleSelector, null, 'border-bottom-color');
this.getControl('color').attachWithSetter(currentColor, function(value) {
CP_Customizer.contentStyle.setProp(styleSelector, null, 'border-bottom-color', value);
})
}
}
}

module.exports = {
sectionArea: {
areaTitle: "WooCommerce Section Options",
init: function($container) {
var color = CP_Customizer.createControl.color(
this.getPrefixed('color'),
$container, {
value: '#ffffff',
label: 'Overlay color'
});
this.addToControlsList(color);
},
update: function(data) {
var dataId = '[data-id=' + data.section.attr('data-id') + ']',
selector =' li .contentswap-effect .overlay',
item = data.section.find(selector).eq(0),
styleSelector =  dataId + selector,
currentGradient = CP_Customizer.contentStyle.getNodeProp(item, styleSelector, null, 'background'),
currentColor = CP_Customizer.styleUtils.getCSSGradientColors(currentGradient,true).pop().value.toHexString();
this.getControl('color').attachWithSetter(currentColor, function(value) {
value = CP_Customizer.styleUtils.generateOneSideLinerGradient(180,value);
CP_Customizer.contentStyle.setProp(styleSelector, null, 'background', value);
})
}
}
}
